import React, { useState } from "react";
import { motion } from "framer-motion";
import { FunnelIcon } from "@heroicons/react/24/outline";

const DataCleaning = () => {
  const [status, setStatus] = useState("idle");
  const [progress, setProgress] = useState(0);
  const [results, setResults] = useState(null);
  const [error, setError] = useState("");

  const startCleaning = async () => {
    setStatus("cleaning");
    setProgress(10);
    setError("");
    setResults(null);

    try {
      setProgress(30); // Show loading state
      const res = await fetch("http://localhost:8000/api/cleaning/start", {
        method: "POST",
      });

      if (!res.ok) throw new Error("Server error");

      setProgress(60);
      const data = await res.json();

      setResults(data);
      setProgress(100);
      setStatus("completed");
    } catch (err) {
      setStatus("error");
      setError("Cleaning failed. Check backend.");
      setProgress(0);
    }
  };

  const badge = {
    idle: "bg-gray-200",
    cleaning: "bg-blue-200",
    completed: "bg-green-200",
    error: "bg-red-200",
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="flex items-center gap-3 mb-6">
        <div className="bg-green-600 p-2 rounded-lg">
          <FunnelIcon className="w-6 h-6 text-white" />
        </div>
        <h1 className="text-2xl font-bold">Data Cleaning Pipeline</h1>
      </div>

      <div className="grid md:grid-cols-2 gap-6">
        {/* Control */}
        <div className="bg-white p-6 rounded-xl shadow">
          <h2 className="font-semibold mb-4">Processing Control</h2>
          <button
            onClick={startCleaning}
            disabled={status === "cleaning"}
            className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 disabled:bg-gray-400"
          >
            {status === "cleaning" ? "Processing..." : "Start Cleaning"}
          </button>

          <div className="mt-4">
            <div className="w-full bg-gray-200 h-3">
              <motion.div
                animate={{ width: `${progress}%` }}
                className="h-3 bg-green-600 rounded"
              />
            </div>
            <p className="text-sm mt-2">{progress}% completed</p>
          </div>

          <span className={`px-3 py-1 rounded text-sm ${badge[status]}`}>
            {status.toUpperCase()}
          </span>

          {error && <p className="text-red-600 mt-3">{error}</p>}
        </div>

        {/* Results */}
        <div className="bg-white p-6 rounded-xl shadow">
          <h2 className="font-semibold mb-4">Cleaning Results</h2>

          {!results && (
            <div className="text-center py-12">
              <div className="bg-white rounded-xl shadow-lg p-8 border-2 border-dashed border-gray-300">
                <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <FunnelIcon className="w-8 h-8 text-gray-400" />
                </div>
                <h3 className="text-xl font-semibold text-gray-700 mb-2">No Data Available</h3>
                <p className="text-gray-600 mb-4">Start the data cleaning process to see results here.</p>

                <div className="space-y-3 text-left">
                  <div className="flex items-center space-x-3 text-gray-600">
                    <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                    <span>No records processed yet</span>
                  </div>
                  <div className="flex items-center space-x-3 text-gray-600">
                    <div className="w-2 h-2 bg-yellow-500 rounded-full"></div>
                    <span>Waiting for data input</span>
                  </div>
